mapper.select:
SELECT e.user_id, e.survey_id, e.is_allowed, e.to_date, e.created_by, e.created_on, e.from_date FROM epass e INNER JOIN ( SELECT user_id, Max(created_on) AS latestDate FROM epass WHERE survey_id = #{survey.surveyId}
AND user_id = #{user.userId}
AND user_id in (select user_id from user where mgr_id = #{user.userId})
GROUP BY user_id ) etemp ON ( e.user_id = etemp.user_id ) AND e.created_on = etemp.latestdate;


select u.first_name,
	u.last_name ,
	(select CONCAT(u2.first_name ," ", u2.last_name) from covid.user u2 where u2.user_id=u.mgr_id) as manager_name,
	e.is_allowed
from covid.epass e, covid.user u
where 
	e.user_id=u.user_id 
	and e.from_date  <=  #{toDate} 
	and e.to_date   >=  #{toDate}
	and u.work_location= #{location}
