mapper.select:
SELECT u.user_id, u.first_name, u.last_name,u.mobile,u.role,u.work_location,u.mgr_id,(select CONCAT(sa.first_name ," ", sa.last_name) from covid.user sa where sa.user_id=u.mgr_id) as mgrName
FROM user u
WHERE u.is_active = 1  

		and u.mobile = #{mobile}



		and u.first_name like  #{pattern}



		and u.last_name like  #{lpattern}


		and u.user_id = #{userId}


		and u.mgr_id = #{mgrID}


		and u.role = #{role}

	ORDER BY u.user_id ASC;

SELECT u.user_id, u.first_name, u.last_name,u.mobile,u.role,u.work_location,u.mgr_id
FROM user u
WHERE u.user_id=#{userId} ;

mapper.insert:
UPDATE user
SET 
	user_id = #{userId}

		,first_name = #{firstName}


		,last_name = #{lastName}


		,work_location = #{workLocation}


		,role = #{role}


		,mobile = #{mobile}


		,mgr_id = #{mgrID}


WHERE 
user_id = #{userId}
