mapper.select:
SELECT u.user_id, u.first_name, u.last_name,u.mobile,u.role,u.work_location
FROM user u
WHERE 1=1  

		and u.mobile = #{mobile}



		and u.first_name like  #{pattern}



		and u.last_name like  #{lpattern}

	ORDER BY u.user_id ASC;

SELECT u.user_id, u.first_name, u.last_name,u.mobile,u.role,u.work_location
FROM user u
WHERE u.user_id=#{userId} ;

mapper.insert:
UPDATE user
SET 
	user_id = #{userId}

		,first_name = #{firstName}


		,last_name = #{lastName}


		,work_location = #{workLocation}


		,role = #{role}

WHERE 
user_id = #{userId}
